import { collection, doc, onSnapshot, serverTimestamp, updateDoc } from "firebase/firestore";
import { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";

export default function OrderTrackingPage() {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);

  // fetch order live
  useEffect(() => {
    const unsub = onSnapshot(doc(db, "orders", orderId), (snap) => {
      if (snap.exists()) setOrder({ id: snap.id, ...snap.data() });
    });
    return () => unsub();
  }, [orderId]);

  // fetch menu live
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "menuItems"), (snap) => {
      setMenu(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, []);

  const addToCart = (item) => {
    setCart(prev => {
      const found = prev.find(p => p.id === item.id);
      if (found) return prev.map(p => p.id === item.id ? { ...p, qty: p.qty + 1 } : p);
      return [...prev, { ...item, qty: 1 }];
    });
  };

  const decQty = (id) => {
    setCart(prev => prev.map(p => p.id === id ? { ...p, qty: p.qty - 1 } : p).filter(p => p.qty > 0));
  };

  const removeItem = (id) => setCart(prev => prev.filter(p => p.id !== id));
  const clearCart = () => setCart([]);

  const total = useMemo(() => cart.reduce((s, i) => s + i.price * i.qty, 0), [cart]);

  const addMoreItems = async () => {
    if (cart.length === 0) return alert("Cart is empty.");

    const newItems = cart.map(i => ({
      id: i.id,
      name: i.name,
      price: i.price,
      qty: i.qty
    }));

    await updateDoc(doc(db, "orders", orderId), {
      items: [...order.items, ...newItems],
      total: (order.total || 0) + total,
      subOrders: [
        ...(order.subOrders || []),
        {
          addedAt: serverTimestamp(),
          items: newItems,
          status: "Pending"
        }
      ]
    });

    clearCart();
    alert("✅ New items added to your order!");
  };

  if (!order) return <div className="card">Loading order...</div>;

  return (
    <div>
      <div className="card">
        <h2>Order Tracking</h2>
        <p><strong>Order ID:</strong> {order.id}</p>
        <p><strong>Name:</strong> {order.customer}</p>
        <p><strong>Table:</strong> {order.table || "-"}</p>
        <p><strong>Status:</strong> {order.status}</p>
        <p><strong>Total:</strong> ₹{order.total}</p>
      </div>

      <div className="card">
        <h3>Items Ordered</h3>
        {order.items?.map((i, idx) => (
          <div key={idx}>
            {i.name} × {i.qty} — ₹{i.price * i.qty}
          </div>
        ))}
      </div>

      {order.subOrders && (
        <div className="card">
          <h3>Added Later</h3>
          {order.subOrders.map((so, idx) => (
            <div key={idx} style={{ marginBottom: 8 }}>
              <strong>{new Date(so.addedAt?.seconds * 1000).toLocaleTimeString()}</strong> — {so.status}
              {so.items.map((i, j) => (
                <div key={j} className="small">{i.name} × {i.qty}</div>
              ))}
            </div>
          ))}
        </div>
      )}

      <div className="card">
        <h3>Add More Items</h3>
        {menu.map(item => (
          <div
            key={item.id}
            style={{
              display: "flex",
              justifyContent: "space-between",
              padding: "6px 0",
              borderBottom: "1px solid #ddd",
              opacity: item.inStock ? 1 : 0.5
            }}
          >
            <span>{item.name}</span>
            <span>₹{item.price}</span>
            {item.inStock ? (
              <button className="btn" onClick={() => addToCart(item)}>Add</button>
            ) : (
              <span style={{ color: "red", fontSize: 12 }}>Out of Stock</span>
            )}
          </div>
        ))}
      </div>

      <div className="card">
        <h3>Your Cart</h3>
        {cart.length === 0 ? <p className="small">Cart is empty</p> : (
          <>
            {cart.map(c => (
              <div key={c.id} style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                  {c.name} — ₹{c.price} × {c.qty}
                </div>
                <div>
                  <button className="btn secondary" onClick={() => decQty(c.id)}>-</button>
                  <button className="btn secondary" onClick={() => addToCart(c)}>+</button>
                  <button className="btn secondary" onClick={() => removeItem(c.id)}>Remove</button>
                </div>
              </div>
            ))}
            <div style={{ marginTop: 10 }}>
              <strong>Total: ₹{total}</strong>
              <button className="btn" style={{ marginLeft: 12 }} onClick={addMoreItems}>
                Add Items to Order
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
