// src/pages/OrderTrackingPage.js
import { collection, doc, getDoc, getDocs, onSnapshot, updateDoc } from "firebase/firestore";
import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";

export default function OrderTrackingPage() {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const prevStatusRef = useRef(null); // ðŸ”” to track previous status

  // âœ… Ask for notification permission once
  useEffect(() => {
    if ("Notification" in window) {
      Notification.requestPermission();
    }
  }, []);

  // Fetch live order
  useEffect(() => {
    if (!orderId) return;
    const unsub = onSnapshot(doc(db, "orders", orderId), (snap) => {
      if (snap.exists()) {
        const newOrder = { id: snap.id, ...snap.data() };

        // ðŸ”” If status changed, show notification
        if (prevStatusRef.current && prevStatusRef.current !== newOrder.status) {
          showStatusNotification(newOrder.status);
        }
        prevStatusRef.current = newOrder.status;

        setOrder(newOrder);
      } else {
        setOrder(null);
      }
    });
    return () => unsub();
  }, [orderId]);

  // âœ… Notification function
  const showStatusNotification = (status) => {
    if (!("Notification" in window)) return;

    const messages = {
      Pending: "Your order has been placed!",
      Preparing: "Your order is being prepared ðŸ³",
      Ready: "Your order is ready to be served ðŸ½ï¸",
      Done: "Your order has been completed âœ…",
      Cancelled: "Your order was cancelled âŒ",
    };

    if (Notification.permission === "granted") {
      new Notification("Order Update", {
        body: messages[status] || `Order status: ${status}`,
        icon: "/logo192.png", // you can use your cafe logo here
      });
    }
  };

  // Fetch menu items
  useEffect(() => {
    const fetchMenu = async () => {
      const snap = await getDocs(collection(db, "menuItems"));
      setMenu(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    };
    fetchMenu();
  }, []);

  const addToCart = (item) => {
    setCart((prev) => {
      const found = prev.find((p) => p.id === item.id);
      if (found) {
        return prev.map((p) => (p.id === item.id ? { ...p, qty: p.qty + 1 } : p));
      }
      return [...prev, { ...item, qty: 1 }];
    });
  };

  const decQty = (id) => {
    setCart((prev) =>
      prev.map((p) => (p.id === id ? { ...p, qty: p.qty - 1 } : p)).filter((p) => p.qty > 0)
    );
  };

  const clearCart = () => setCart([]);

  const total = cart.reduce((s, i) => s + i.price * i.qty, 0);

  // âœ… Append new items to same order
  const addItemsToOrder = async () => {
    if (cart.length === 0) return alert("Cart is empty.");

    const orderRef = doc(db, "orders", orderId);
    const snap = await getDoc(orderRef);

    if (!snap.exists()) return alert("Order not found.");

    const existing = snap.data();
    const existingItems = existing.items || [];

    const updatedItems = [...existingItems, ...cart.map((i) => ({
      id: i.id,
      name: i.name,
      price: i.price,
      qty: i.qty
    }))];

    const newTotal = updatedItems.reduce((s, i) => s + i.price * i.qty, 0);

    await updateDoc(orderRef, {
      items: updatedItems,
      total: newTotal,
      updatedAt: new Date()
    });

    clearCart();
  };

  // âœ… Cancel order
  const cancelOrder = async () => {
    if (!order) return;
    if (!(order.status === "Pending" || order.status === "Preparing")) {
      return alert("Order cannot be cancelled now.");
    }
    if (window.confirm("Are you sure you want to cancel this order?")) {
      const orderRef = doc(db, "orders", order.id);
      await updateDoc(orderRef, { status: "Cancelled" });
      alert("Order cancelled.");
    }
  };

  if (!order) return <p>Loading order...</p>;

  return (
    <div>
      <h2>Order Tracking</h2>
      <p><strong>Order ID:</strong> {order.id}</p>
      <p><strong>Customer:</strong> {order.customer}</p>
      <p><strong>Status:</strong> {order.status}</p>
      <p><strong>Total Bill:</strong> â‚¹{order.total}</p>

      {(order.status === "Pending" || order.status === "Preparing") && (
        <button className="btn danger" onClick={cancelOrder}>
          Cancel Order
        </button>
      )}

      <h3>Items</h3>
      <ul>
        {order.items?.map((i, idx) => (
          <li key={idx}>
            {i.name} Ã— {i.qty} â€” â‚¹{i.price * i.qty}
          </li>
        ))}
      </ul>

      <h3>Add More Items</h3>
      <div>
        {menu.map((item) => (
          <div key={item.id} style={{ marginBottom: 8 }}>
            <span>{item.name} â€” â‚¹{item.price}</span>
            {item.inStock ? (
              <button className="btn" onClick={() => addToCart(item)}>Add</button>
            ) : (
              <span style={{ color: "red", marginLeft: 8 }}>Out of stock</span>
            )}
          </div>
        ))}
      </div>

      <h3>Your New Cart</h3>
      {cart.length === 0 ? (
        <p>No new items added.</p>
      ) : (
        <div>
          {cart.map((c) => (
            <div key={c.id} style={{ marginBottom: 8 }}>
              <strong>{c.name}</strong> â€” â‚¹{c.price} Ã— {c.qty}
              <button className="btn secondary" onClick={() => decQty(c.id)}>-</button>
              <button className="btn secondary" onClick={() => addToCart(c)}>+</button>
            </div>
          ))}
          <p><strong>Total for new items: â‚¹{total}</strong></p>
          <button className="btn" onClick={addItemsToOrder}>Add to Order</button>
          <button className="btn secondary" onClick={clearCart} style={{ marginLeft: 8 }}>Clear</button>
        </div>
      )}
    </div>
  );
}
