// src/pages/CashierPage.js
import {
  onAuthStateChanged, signInWithEmailAndPassword, signOut
} from "firebase/auth";
import {
  addDoc,
  collection,
  deleteDoc,
  doc, onSnapshot, orderBy, query, updateDoc
} from "firebase/firestore";
import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, db } from "../firebase";

export default function CashierPage() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [orders, setOrders] = useState([]);
  const [showDone, setShowDone] = useState(false);
  const [taxes, setTaxes] = useState([]);
const [newTax, setNewTax] = useState({ name: "", percentage: "" });

  useEffect(() => {
    const unsubAuth = onAuthStateChanged(auth, (u) => setUser(u));
    return () => unsubAuth();
  }, []);

  useEffect(() => {
    if (!user) { setOrders([]); return; }
    const q = query(collection(db, "orders"), orderBy("createdAt", "asc"));
    const unsub = onSnapshot(q, (snap) => {
      setOrders(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, [user]);

    useEffect(() => {
    if (!user) { setTaxes([]); return; }
    const unsub = onSnapshot(collection(db, "taxes"), (snap) => {
      setTaxes(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, [user]);

  // Add tax
  const addTax = async (e) => {
    e.preventDefault();
    if (!newTax.name || !newTax.percentage) return alert("Enter tax name and %");
    await addDoc(collection(db, "taxes"), {
      name: newTax.name,
      percentage: parseFloat(newTax.percentage),
    });
    setNewTax({ name: "", percentage: "" });
  };

  // Update tax
  const updateTax = async (id, updates) => {
    await updateDoc(doc(db, "taxes", id), updates);
  };

  // Delete tax
  const deleteTax = async (id) => {
    if (window.confirm("Delete this tax?")) {
      await deleteDoc(doc(db, "taxes", id));
    }
  };

  const navigate = useNavigate();

  // ðŸ”‘ Helper to update a specific batch status
  const setBatchStatus = async (orderId, batchId, status) => {
    const orderRef = doc(db, "orders", orderId);
    const order = orders.find((o) => o.id === orderId);
    if (!order) return;

    const updatedBatches = order.batches.map((b) =>
      b.batchId === batchId ? { ...b, status } : b
    );

    await updateDoc(orderRef, {
      batches: updatedBatches,
      status, // keep parent order status in sync with last change
    });
  };

  // ðŸ”‘ Flatten batches into separate rows
  const allBatches = useMemo(() => {
    let rows = [];
    orders.forEach((o) => {
      (o.batches || []).forEach((b) => {
        rows.push({ ...o, batch: b });
      });
    });
    return rows;
  }, [orders]);

  const pending = useMemo(() => allBatches.filter((r) => r.batch.status === "Pending"), [allBatches]);
  const preparing = useMemo(() => allBatches.filter((r) => r.batch.status === "Preparing"), [allBatches]);
  const ready = useMemo(() => allBatches.filter((r) => r.batch.status === "Ready"), [allBatches]);
  const done = useMemo(() => allBatches.filter((r) => r.batch.status === "Done"), [allBatches]);

  const todayDone = useMemo(() => {
    const todayStr = new Date().toLocaleDateString();
    return done.filter(r => {
      if (!r.batch.createdAt?.seconds) return false;
      const d = new Date(r.batch.createdAt.seconds * 1000).toLocaleDateString();
      return d === todayStr;
    });
  }, [done]);

  const groupedDone = useMemo(() => {
    const groups = {};
    done.forEach(r => {
      const dateStr = new Date(r.batch.createdAt?.seconds * 1000).toLocaleDateString();
      if (!groups[dateStr]) groups[dateStr] = [];
      groups[dateStr].push(r);
    });
    return groups;
  }, [done]);

  const handleLogin = async (e) => {
    e.preventDefault();
    await signInWithEmailAndPassword(auth, email, password);
    setEmail(""); setPassword("");
  };

  if (!user) {
    return (
      <div className="card" style={{ maxWidth: 420, margin: "0 auto" }}>
        <h3>Cashier Login</h3>
        <form onSubmit={handleLogin} style={{ display: "grid", gap: 8 }}>
          <input className="input" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input className="input" type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button className="btn" type="submit">Sign in</button>
        </form>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
        <h2>Cashier Dashboard</h2>
        <button className="btn" onClick={() => navigate("/menu-management")}>
          Menu Management
        </button>
        <div>
          <span style={{ marginRight: 8 }}>Signed in: {user.email}</span>
          <button className="btn secondary" onClick={() => signOut(auth)}>Sign out</button>
        </div>
      </div>

      {/* ---- TAXES MANAGEMENT ---- */}
      <div className="section" style={{ marginBottom: 20 }}>
        <h3>Manage Taxes</h3>

        <form onSubmit={addTax} style={{ display: "flex", gap: 8, marginBottom: 12 }}>
          <input
            placeholder="Tax Name (e.g. GST)"
            value={newTax.name}
            onChange={(e) => setNewTax({ ...newTax, name: e.target.value })}
          />
          <input
            type="number"
            placeholder="%"
            value={newTax.percentage}
            onChange={(e) => setNewTax({ ...newTax, percentage: e.target.value })}
          />
          <button className="btn" type="submit">Add</button>
        </form>

        {taxes.map((tax) => (
          <div key={tax.id} className="order-card" style={{ display: "flex", justifyContent: "space-between" }}>
            <div>
              <strong>{tax.name}</strong> Â· {tax.percentage}%
            </div>
            <div>
              <button
                className="btn secondary"
                onClick={() => {
                  const newPercent = prompt("Enter new %:", tax.percentage);
                  if (newPercent) updateTax(tax.id, { percentage: parseFloat(newPercent) });
                }}
              >
                Edit
              </button>
              <button className="btn danger" style={{ marginLeft: 8 }} onClick={() => deleteTax(tax.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>


      {/* Pending Section */}
      <div className="section">
        <h4 style={{ padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}>
          Pending ({pending.length})
        </h4>
        {pending.map(r => (
          <div key={`${r.id}-${r.batch.batchId}`} className="order-card">
            <div className="order-left">
              <div><strong>{r.customer}</strong> {r.table ? `Â· Table ${r.table}` : ""}</div>
              <div className="small">{r.batch.items?.map(i => `${i.name}Ã—${i.qty}`).join(" , ")}</div>
              <div className="small">Batch Total: â‚¹{r.batch.items.reduce((s, i) => s + i.price * i.qty, 0)}</div>
            </div>
            <div style={{ textAlign: "right" }}>
              <div className={"status-badge status-Pending"}>Pending</div>
              <div style={{ marginTop: 8 }}>
                <button className="btn" onClick={() => setBatchStatus(r.id, r.batch.batchId, "Preparing")}>Start Preparing</button>
                <button className="btn secondary" style={{ marginLeft: 8 }} onClick={() => setBatchStatus(r.id, r.batch.batchId, "Ready")}>Mark Ready</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Preparing Section */}
      <div className="section">
        <h4 style={{ padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}>
          Preparing ({preparing.length})
        </h4>
        {preparing.map(r => (
          <div key={`${r.id}-${r.batch.batchId}`} className="order-card">
            <div className="order-left">
              <div><strong>{r.customer}</strong></div>
              <div className="small">{r.batch.items?.map(i => `${i.name}Ã—${i.qty}`).join(" , ")}</div>
            </div>
            <div style={{ textAlign: "right" }}>
              <div className={"status-badge status-Preparing"}>Preparing</div>
              <div style={{ marginTop: 8 }}>
                <button className="btn" onClick={() => setBatchStatus(r.id, r.batch.batchId, "Ready")}>Mark Ready</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Ready Section */}
      <div className="section">
        <h4 style={{ padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}>
          Ready ({ready.length})
        </h4>
        {ready.map(r => (
          <div key={`${r.id}-${r.batch.batchId}`} className="order-card">
            <div className="order-left">
              <div><strong>{r.customer}</strong></div>
              <div className="small">{r.batch.items?.map(i => `${i.name}Ã—${i.qty}`).join(" , ")}</div>
            </div>
            <div style={{ textAlign: "right" }}>
              <div className={"status-badge status-Ready"}>Ready</div>
              <div style={{ marginTop: 8 }}>
                <button className="btn" onClick={() => setBatchStatus(r.id, r.batch.batchId, "Done")}>Mark Done</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Done Section */}
      <div className="section">
        <h4
          style={{ cursor: "pointer", padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}
          onClick={() => setShowDone(!showDone)}
        >
          Done ({todayDone.length}) {showDone ? "â–²" : "â–¼"}
        </h4>
        {showDone && Object.entries(groupedDone).map(([date, list]) => {
          const totalAmt = list.reduce((sum, r) => sum + r.batch.items.reduce((s, i) => s + i.price * i.qty, 0), 0);

          return (
            <div key={date} style={{ marginBottom: 12, paddingLeft: 12 }}>
              <h5>{date} â€” {list.length} batches Â· â‚¹{totalAmt}</h5>
              {list.map(r => (
                <div key={`${r.id}-${r.batch.batchId}`} className="order-card">
                  <div className="order-left">
                    <div><strong>{r.customer}</strong></div>
                    <div className="small">{r.batch.items?.map(i => `${i.name}Ã—${i.qty}`).join(" , ")}</div>
                    <div className="small">Batch Total: â‚¹{r.batch.items.reduce((s, i) => s + i.price * i.qty, 0)}</div>
                  </div>
                  <div style={{ textAlign: "right" }}>
                    <div className={"status-badge status-Done"}>Done</div>
                  </div>
                </div>
              ))}
            </div>
          );
        })}
      </div>
    </div>
  );
}
