// src/pages/OrderTrackingPage.js
import { collection, doc, getDoc, getDocs, onSnapshot, updateDoc } from "firebase/firestore";
import { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";

export default function OrderTrackingPage() {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [taxes, setTaxes] = useState([]);
  const prevStatusRef = useRef(null);

  // âœ… Ask for notification permission once
  useEffect(() => {
    if ("Notification" in window) {
      Notification.requestPermission();
    }
  }, []);

  // âœ… Fetch live order with batches
  useEffect(() => {
    if (!orderId) return;
    const unsub = onSnapshot(doc(db, "orders", orderId), (snap) => {
      if (snap.exists()) {
        const newOrder = { id: snap.id, ...snap.data() };

        // ðŸ”” Only check parent order status
        if (prevStatusRef.current && prevStatusRef.current !== newOrder.status) {
          showStatusNotification(newOrder.status);
        }
        prevStatusRef.current = newOrder.status;

        setOrder(newOrder);
      } else {
        setOrder(null);
      }
    });
    return () => unsub();
  }, [orderId]);

  // âœ… Fetch taxes
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "taxes"), (snap) => {
      setTaxes(snap.docs.map(d => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, []);

  // âœ… Notification function
  const showStatusNotification = (status) => {
    if (!("Notification" in window)) return;

    const messages = {
      Pending: "Your order has been placed!",
      Preparing: "Your order is being prepared ðŸ³",
      Ready: "Your order is ready to be served ðŸ½ï¸",
      Done: "Your order has been completed âœ…",
      Cancelled: "Your order was cancelled âŒ",
    };

    if (Notification.permission === "granted") {
      new Notification("Order Update", {
        body: messages[status] || `Order status: ${status}`,
        icon: "/logo192.png",
      });
    }
  };

  // Fetch menu items
  useEffect(() => {
    const fetchMenu = async () => {
      const snap = await getDocs(collection(db, "menuItems"));
      setMenu(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    };
    fetchMenu();
  }, []);

  const addToCart = (item) => {
    setCart((prev) => {
      const found = prev.find((p) => p.id === item.id);
      if (found) {
        return prev.map((p) => (p.id === item.id ? { ...p, qty: p.qty + 1 } : p));
      }
      return [...prev, { ...item, qty: 1 }];
    });
  };

  const decQty = (id) => {
    setCart((prev) =>
      prev.map((p) => (p.id === id ? { ...p, qty: p.qty - 1 } : p)).filter((p) => p.qty > 0)
    );
  };

  const clearCart = () => setCart([]);

  const total = cart.reduce((s, i) => s + i.price * i.qty, 0);

  // âœ… Add new batch instead of merging
  const addItemsToOrder = async () => {
    if (cart.length === 0) return alert("Cart is empty.");

    const orderRef = doc(db, "orders", orderId);
    const snap = await getDoc(orderRef);

    if (!snap.exists()) return alert("Order not found.");

    const existing = snap.data();
    const existingBatches = existing.batches || [];

    const newBatch = {
      batchId: Date.now().toString(),
      items: cart.map((i) => ({
        id: i.id,
        name: i.name,
        price: i.price,
        qty: i.qty,
      })),
      status: "Pending",
      createdAt: new Date(),
    };

    const newTotal =
      existing.total +
      cart.reduce((s, i) => s + i.price * i.qty, 0);

    await updateDoc(orderRef, {
      batches: [...existingBatches, newBatch],
      total: newTotal,
      updatedAt: new Date(),
    });

    clearCart();
  };

  // âœ… Cancel order (only if all batches still pending/preparing)
  const cancelOrder = async () => {
    if (!order) return;
    const cancellable = order.batches?.every(
      (b) => b.status === "Pending" || b.status === "Preparing"
    );
    if (!cancellable) {
      return alert("Some items are already processed. Cannot cancel.");
    }

    if (window.confirm("Are you sure you want to cancel this order?")) {
      const orderRef = doc(db, "orders", order.id);
      await updateDoc(orderRef, { status: "Cancelled" });
      alert("Order cancelled.");
    }
  };

  // âœ… Digital Bill Generator
  const generateBill = () => {
    if (!order) return;
    let bill = `--- Digital Bill ---\nCustomer: ${order.customer}\nOrder ID: ${order.id}\n\nItems:\n`;

    order.batches.forEach(batch => {
      batch.items.forEach(i => {
        bill += `${i.name} x${i.qty} = â‚¹${i.price * i.qty}\n`;
      });
    });

    bill += `\nSubtotal: â‚¹${order.total}`;

    // Apply taxes
    let taxTotal = 0;
    taxes.forEach(t => {
      const taxAmt = (order.total * t.percentage) / 100;
      taxTotal += taxAmt;
      bill += `\n${t.name} (${t.percentage}%): â‚¹${taxAmt.toFixed(2)}`;
    });

    const finalTotal = order.total + taxTotal;
    bill += `\n\nGrand Total: â‚¹${finalTotal.toFixed(2)}\n-------------------`;

    // Print or download
    const blob = new Blob([bill], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `Bill_${order.id}.txt`;
    link.click();
  };

  if (!order) return <p>Loading order...</p>;

  return (
    <div>
      <h2>Order Tracking</h2>
      <p><strong>Order ID:</strong> {order.id}</p>
      <p><strong>Customer:</strong> {order.customer}</p>
      <p><strong>Status:</strong> {order.status}</p>
      <p><strong>Subtotal:</strong> â‚¹{order.total}</p>

      {/* âœ… Show tax breakdown only when Done */}
      {order.status === "Done" && (
        <div style={{ marginTop: 12, padding: 10, border: "1px solid #ccc" }}>
          <h3>Bill Summary</h3>
          <p>Subtotal: â‚¹{order.total}</p>
          {taxes.map(t => {
            const taxAmt = (order.total * t.percentage) / 100;
            return (
              <p key={t.id}>
                {t.name} ({t.percentage}%): â‚¹{taxAmt.toFixed(2)}
              </p>
            );
          })}
          <p>
            <strong>Grand Total: â‚¹
              {(order.total + taxes.reduce((s, t) => s + (order.total * t.percentage) / 100, 0)).toFixed(2)}
            </strong>
          </p>
          <button className="btn" onClick={generateBill}>Generate Digital Bill</button>
        </div>
      )}

      {(order.status === "Pending") && (
        <button className="btn danger" onClick={cancelOrder}>
          Cancel Order
        </button>
      )}

      <h3>Batches</h3>
      {order.batches?.map((batch) => (
        <div key={batch.batchId} style={{ marginBottom: 12, border: "1px solid #ddd", padding: 8 }}>
          <p><strong>Batch:</strong> {batch.batchId}</p>
          <p><strong>Status:</strong> {batch.status}</p>
          <ul>
            {batch.items.map((i, idx) => (
              <li key={idx}>
                {i.name} Ã— {i.qty} â€” â‚¹{i.price * i.qty}
              </li>
            ))}
          </ul>
        </div>
      ))}

      <h3>Add More Items</h3>
      <div>
        {menu.map((item) => (
          <div key={item.id} style={{ marginBottom: 8 }}>
            <span>{item.name} â€” â‚¹{item.price}</span>
            {item.inStock ? (
              <button className="btn" onClick={() => addToCart(item)}>Add</button>
            ) : (
              <span style={{ color: "red", marginLeft: 8 }}>Out of stock</span>
            )}
          </div>
        ))}
      </div>

      <h3>Your New Cart</h3>
      {cart.length === 0 ? (
        <p>No new items added.</p>
      ) : (
        <div>
          {cart.map((c) => (
            <div key={c.id} style={{ marginBottom: 8 }}>
              <strong>{c.name}</strong> â€” â‚¹{c.price} Ã— {c.qty}
              <button className="btn secondary" onClick={() => decQty(c.id)}>-</button>
              <button className="btn secondary" onClick={() => addToCart(c)}>+</button>
            </div>
          ))}
          <p><strong>Total for new items: â‚¹{total}</strong></p>
          <button className="btn" onClick={addItemsToOrder}>Add to Order</button>
          <button className="btn secondary" onClick={clearCart} style={{ marginLeft: 8 }}>Clear</button>
        </div>
      )}
    </div>
  );
}
