// src/pages/CashierPage.js
import {
  onAuthStateChanged, signInWithEmailAndPassword, signOut
} from "firebase/auth";
import {
  collection,
  doc, getDocs, onSnapshot, orderBy, query, updateDoc
} from "firebase/firestore";
import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, db } from "../firebase";

export default function CashierPage() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [orders, setOrders] = useState([]);
  const [showDone, setShowDone] = useState(false);
  const [taxes, setTaxes] = useState([]);

  useEffect(() => {
    const unsubAuth = onAuthStateChanged(auth, (u) => setUser(u));
    return () => unsubAuth();
  }, []);

  useEffect(() => {
    if (!user) { setOrders([]); return; }
    const q = query(collection(db, "orders"), orderBy("createdAt", "asc"));
    const unsub = onSnapshot(q, (snap) => {
      setOrders(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, [user]);

  // âœ… Fetch taxes from Firestore
  useEffect(() => {
    const fetchTaxes = async () => {
      const snap = await getDocs(collection(db, "taxes"));
      setTaxes(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    };
    fetchTaxes();
  }, []);

  const navigate = useNavigate();

  const setBatchStatus = async (orderId, batchId, status) => {
    const orderRef = doc(db, "orders", orderId);
    const order = orders.find((o) => o.id === orderId);
    if (!order) return;

    const updatedBatches = order.batches.map((b) =>
      b.batchId === batchId ? { ...b, status } : b
    );

    await updateDoc(orderRef, {
      batches: updatedBatches,
      status,
    });
  };

  // âœ… Flatten batches only for active sections
  const allBatches = useMemo(() => {
    let rows = [];
    orders.forEach((o) => {
      (o.batches || []).forEach((b) => {
        rows.push({ ...o, batch: b });
      });
    });
    return rows;
  }, [orders]);

  const pending = useMemo(() => allBatches.filter((r) => r.batch.status === "Pending"), [allBatches]);
  const preparing = useMemo(() => allBatches.filter((r) => r.batch.status === "Preparing"), [allBatches]);
  const ready = useMemo(() => allBatches.filter((r) => r.batch.status === "Ready"), [allBatches]);

  // âœ… Group done orders (fully completed)
  const doneOrders = useMemo(() => {
    return orders.filter(o =>
      (o.batches || []).every(b => b.status === "Done")
    );
  }, [orders]);

  const doneUnpaid = useMemo(() => {
    return doneOrders.filter(o => o.paymentStatus !== "Paid");
  }, [doneOrders]);

  const paymentDone = useMemo(() => {
    return orders.filter(o => o.status === "PaymentDone" && o.paymentStatus === "Paid");
  }, [orders]);

  // âœ… Generate combined bill for whole order
  const generateBill = (order) => {
    let subtotal = 0;
    let itemsList = [];

    order.batches.forEach(batch => {
      batch.items.forEach(i => {
        subtotal += i.price * i.qty;
        itemsList.push(`${i.name} Ã— ${i.qty} = â‚¹${i.price * i.qty}`);
      });
    });

    // ðŸ”¹ Apply discount
    let discountAmount = 0;
    if (order.discount) {
      if (order.discount.type === "percentage") {
        discountAmount = (subtotal * order.discount.value) / 100;
      } else {
        discountAmount = order.discount.value;
      }
    }

    const taxable = subtotal - discountAmount;

    // ðŸ”¹ Apply taxes
    let taxDetails = "";
    let totalTax = 0;
    taxes.forEach(t => {
      const amt = (taxable * t.percentage) / 100;
      taxDetails += `${t.name} (${t.percentage}%): â‚¹${amt.toFixed(2)}\n`;
      totalTax += amt;
    });

    const finalTotal = taxable + totalTax;

    const bill = `
    DIGITAL BILL
    ------------------------
    Customer: ${order.customer}
    Table: ${order.table || "-"}
    Order ID: ${order.id}
    ------------------------
    Items:
    ${itemsList.join("\n")}
    ------------------------
    Subtotal: â‚¹${subtotal.toFixed(2)}
    Discount: -â‚¹${discountAmount.toFixed(2)}
    Taxable: â‚¹${taxable.toFixed(2)}
    ${taxDetails}
    Final Total: â‚¹${finalTotal.toFixed(2)}
    ------------------------
    Thank you for visiting!
    `;

    const blob = new Blob([bill], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `Bill_${order.id}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    await signInWithEmailAndPassword(auth, email, password);
    setEmail(""); setPassword("");
  };

  if (!user) {
    return (
      <div className="card" style={{ maxWidth: 420, margin: "0 auto" }}>
        <h3>Cashier Login</h3>
        <form onSubmit={handleLogin} style={{ display: "grid", gap: 8 }}>
          <input className="input" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input className="input" type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button className="btn" type="submit">Sign in</button>
        </form>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
        <h2>Cashier Dashboard</h2>
        <button className="btn" onClick={() => navigate("/menu-management")}>Menu Management</button>
        <button className="btn" onClick={() => navigate("/tax-management")}>Manage Taxes</button>
        <div>
          <span style={{ marginRight: 8 }}>Signed in: {user.email}</span>
          <button className="btn secondary" onClick={() => signOut(auth)}>Sign out</button>
        </div>
      </div>

      {/* Pending Section */}
      {/* ... keep Pending, Preparing, Ready same as before ... */}

      {/* Done Section (Unpaid) */}
      <div className="section">
        <h4
          style={{ cursor: "pointer", padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}
          onClick={() => setShowDone(!showDone)}
        >
          Done (Unpaid: {doneUnpaid.length}) {showDone ? "â–²" : "â–¼"}
        </h4>

        {showDone && doneUnpaid.map(order => (
          <div key={order.id} className="order-card">
            <div className="order-left">
              <div><strong>{order.customer}</strong></div>
              <div className="small">
                {order.batches.flatMap(b => b.items.map(i => `${i.name}Ã—${i.qty}`)).join(" , ")}
              </div>
              <div className="small">Total: â‚¹{order.total}</div>
              {order.discount && (
                <div className="small" style={{ color: "green" }}>
                  Discount Applied: {order.discount.type === "percentage"
                    ? `${order.discount.value}%`
                    : `â‚¹${order.discount.value}`}
                </div>
              )}
            </div>

            <div style={{ textAlign: "right" }}>
              <div className={"status-badge status-Done"}>Done - {order.paymentStatus || "Not Paid"}</div>

              {/* Discount input */}
              <div style={{ marginTop: 8 }}>
                <input
                  className="input"
                  style={{ width: "100px", marginRight: 8 }}
                  placeholder="10% or 100"
                  defaultValue={
                    order.discount
                      ? order.discount.type === "percentage"
                        ? `${order.discount.value}%`
                        : order.discount.value
                      : ""
                  }
                  onBlur={async (e) => {
                    const val = e.target.value.trim();
                    let discount = null;
                    if (val.endsWith("%")) {
                      discount = { type: "percentage", value: parseFloat(val.replace("%", "")) };
                    } else if (!isNaN(val) && val !== "") {
                      discount = { type: "flat", value: parseFloat(val) };
                    }

                    await updateDoc(doc(db, "orders", order.id), { discount });
                  }}
                />
              </div>

              {/* Mark Paid Button */}
              <button
                className="btn"
                style={{ marginTop: 8 }}
                onClick={async () => {
                  await updateDoc(doc(db, "orders", order.id), {
                    paymentStatus: "Paid",
                    status: "PaymentDone"
                  });
                }}
              >
                Mark as Paid
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Payment Done Section */}
      <div className="section">
        <h4 style={{ padding: "8px", background: "#c6c2c2ff", borderRadius: 4 }}>
          Payment Done ({paymentDone.length})
        </h4>

        {paymentDone.map(order => (
          <div key={order.id} className="order-card">
            <div className="order-left">
              <div><strong>{order.customer}</strong></div>
              <div className="small">
                {order.batches.flatMap(b => b.items.map(i => `${i.name}Ã—${i.qty}`)).join(" , ")}
              </div>
              <div className="small">Total: â‚¹{order.total}</div>
              {order.discount && (
                <div className="small" style={{ color: "green" }}>
                  Discount Applied: {order.discount.type === "percentage"
                    ? `${order.discount.value}%`
                    : `â‚¹${order.discount.value}`}
                </div>
              )}
            </div>

            <div style={{ textAlign: "right" }}>
              <div className={"status-badge status-PaymentDone"}>Payment Done</div>
              <button
                className="btn"
                style={{ marginTop: 8 }}
                onClick={() => generateBill(order)}
              >
                Generate Bill
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
