// src/pages/CashierPage.js
import {
  onAuthStateChanged, signInWithEmailAndPassword, signOut
} from "firebase/auth";
import {
  collection,
  doc, onSnapshot, orderBy, query, updateDoc
} from "firebase/firestore";
import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, db } from "../firebase";

export default function CashierPage() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [orders, setOrders] = useState([]);
  const [showDone, setShowDone] = useState(false);

  useEffect(() => {
    const unsubAuth = onAuthStateChanged(auth, (u) => setUser(u));
    return () => unsubAuth();
  }, []);

  useEffect(() => {
    if (!user) { setOrders([]); return; }
    const q = query(collection(db, "orders"), orderBy("createdAt", "asc"));
    const unsub = onSnapshot(q, (snap) => {
      setOrders(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });
    return () => unsub();
  }, [user]);

  const navigate = useNavigate();

  // ðŸ”‘ Helper to update a specific batch status
  const setBatchStatus = async (orderId, batchId, status) => {
    const orderRef = doc(db, "orders", orderId);
    const order = orders.find((o) => o.id === orderId);
    if (!order) return;

    const updatedBatches = order.batches.map((b) =>
      b.batchId === batchId ? { ...b, status } : b
    );

    await updateDoc(orderRef, {
      batches: updatedBatches,
      status, // keep parent order status in sync with last change
    });
  };

  // ðŸ”‘ Flatten batches into separate rows
  const allBatches = useMemo(() => {
    let rows = [];
    orders.forEach((o) => {
      (o.batches || []).forEach((b) => {
        rows.push({ ...o, batch: b });
      });
    });
    return rows;
  }, [orders]);

  const pending = useMemo(() => allBatches.filter((r) => r.batch.status === "Pending"), [allBatches]);
  const preparing = useMemo(() => allBatches.filter((r) => r.batch.status === "Preparing"), [allBatches]);
  const ready = useMemo(() => allBatches.filter((r) => r.batch.status === "Ready"), [allBatches]);
  const done = useMemo(() => allBatches.filter((r) => r.batch.status === "Done"), [allBatches]);

  const todayDone = useMemo(() => {
    const todayStr = new Date().toLocaleDateString();
    return done.filter(r => {
      if (!r.batch.createdAt?.seconds) return false;
      const d = new Date(r.batch.createdAt.seconds * 1000).toLocaleDateString();
      return d === todayStr;
    });
  }, [done]);

  const groupedDone = useMemo(() => {
    const groups = {};
    done.forEach(r => {
      const dateStr = new Date(r.batch.createdAt?.seconds * 1000).toLocaleDateString();
      if (!groups[dateStr]) groups[dateStr] = [];
      groups[dateStr].push(r);
    });
    return groups;
  }, [done]);

  const handleLogin = async (e) => {
    e.preventDefault();
    await signInWithEmailAndPassword(auth, email, password);
    setEmail(""); setPassword("");
  };

  if (!user) {
    return (
      <div className="card" style={{ maxWidth: 420, margin: "0 auto" }}>
        <h3>Cashier Login</h3>
        <form onSubmit={handleLogin} style={{ display: "grid", gap: 8 }}>
          <input className="input" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input className="input" type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button className="btn" type="submit">Sign in</button>
        </form>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
